INSERT INTO `cms_content_ext` VALUES ('34', '我国有望后年发射火星探测器', null, null, null, null, '据中国空间技术研究院有关专家介绍，2013年，我国有望利用长征三号乙运载火箭发射自主火星探测器。我国航天工业部门已先期启动了基于探月一二期技术的自主火星探测器研究和方案设计工作，目前正在积极开展技术攻关。据悉，我国自主火星探测器的科学载荷重量达100公斤以上，科学探测能力将大大提高。', '2011-01-03 21:38:30', null, null, null, '0', null, null, null, null, null, '1');
INSERT INTO `cms_content_ext` VALUES ('35', '首都机场30个赴欧美航班因欧美暴雪延误', null, null, null, null, null, '2011-01-03 21:38:31', null, null, null, '0', null, null, null, null, null, '1');
