INSERT INTO `cms_content_ext` VALUES ('39', '婚姻登记信息5年内全国联网 北京等3地今年试点', null, null, null, null, '民政部日前召开婚姻登记规范化视频会议。会上透露，截至目前，全国多个省市婚登机关已实现婚姻登记网上预约、在线查询、婚姻档案信息检索等功能。婚姻登记信息实现省级联网的省份已由2005年的2个增加到现在的23个，省级联网率达到了74%。“十二五”期间，婚姻登记信息将实现全国联网。', '2011-01-03 21:38:36', null, null, null, '0', null, null, null, null, null, '1');
