INSERT INTO `cms_content_ext` VALUES ('173', '千千静听', '千千静听(TTPlayer) V5.7 BETA1', null, null, null, '千千静听是一款完全免费的音乐播放软件，拥有自主研发的全新音频引擎，集播放、音效、转换、歌词等众多功能于一身。其小巧精致、操作简捷、功能强大的特点，深得用户喜爱，被网友评为中国十大优秀软件之一，并且成为目前国内最受欢迎的音乐播放软件。', '2011-01-04 14:44:02', null, null, null, '0', '/u/cms/www/201112/19141200ip5c.jpg', 'http://ttplayer.qianqian.com/upload/100902/100902162707s.jpg', null, null, null, '1');
