INSERT INTO `cms_content_ext` VALUES ('243', '中国语文教育已经到了最危险的时刻', null, null, null, null, '廖增湖还有个身份——作家。在圈子里，他笔名叶开，素来以阅读面宽阔文章不落俗套著称，几部长篇小说，口碑很好，被评论界称为“上海的王朔，中国的拉伯雷”。如今是知名文学杂志《收获》的编审。\r\n\r\n', '2011-12-19 08:53:01', null, null, null, '0', null, null, '/u/cms/www/201112/19090536qbxp.jpg', null, null, '1');
