INSERT INTO `cms_content_ext` VALUES ('271', '思路摄影【闺蜜糖心派】', null, null, null, null, '我深信这世上有些孩子一直都是天使，所以，请你要一直这样地纯真着。对于这世间所有的烈日风霜，一定要相信，你不只是一个人手心里的宝。\r\n   愿天下所有的闺蜜快乐健康！~~', '2011-12-19 09:45:20', null, null, null, '0', null, null, '/u/cms/www/201112/19094631wo80.jpg', null, null, '1');
