INSERT INTO `cms_content_ext` VALUES ('286', '“奶茶MM”加入清华啦啦队 激情热舞展现青春活力', null, null, null, null, '近日，国家体育总局体操运动管理中心主办、北京市大学生体操协会“两操”分会和全国啦啦操竞赛委员会承办的2011年全国啦啦操联赛北京站比赛在京举行。来自清华大学的“奶茶MM”章泽天激情热舞格外抢眼，成本次比赛话题人物。', '2011-12-19 09:58:29', null, null, null, '0', null, null, '/u/cms/www/201112/19095752235i.jpg', null, null, '1');
