INSERT INTO `cms_content_ext` VALUES ('321', '童星今昔对比照大曝光', null, null, null, null, '小龙人、霹雳贝贝、红孩儿......一个个耳熟能详的名字，一个个陪伴我们度过美好童年的小明星，时过境迁，如今又是什么模样？本文将带你一起领略昔日同学现在的风采。', '2011-12-19 11:31:57', null, null, null, '0', null, null, '/u/cms/www/201112/19113155zjrt.jpg', null, null, '1');
