INSERT INTO `cms_topic` VALUES ('1', null, '2010年南非世界杯', '世界杯', '世界杯', '2010年世界杯将在南非约翰内斯堡拉开帷幕，32路豪强将在一个月的时间里，为大力神杯展开争夺。', 'http://a2.att.hudong.com/08/61/01300000406647124377613651616.jpg', 'http://i0.sinaimg.cn/ty/news/2010/0611/sjbsc.jpg', '', '10', '1');
INSERT INTO `cms_topic` VALUES ('2', null, '上海世博会专题000', '世博', '世博', '人类文明的盛会，我们大家的世博，精彩开篇，“满月”有余。随着上海世博会的有序前行，人们从中收获的感悟也由表及里。', 'http://xwcb.eastday.com/c/20061116/images/00033531.jpg', '/u/cms/www/201112/19151533k5ey.jpg', '', '10', '1');
INSERT INTO `cms_topic` VALUES ('3', null, '低碳经济', '低碳', '低碳', '所谓低碳经济，是指在可持续发展理念指导下，通过技术创新、制度创新、产业转型、新能源开发等多种手段，尽可能地减少煤炭石油等高碳能源消耗，减少温室气体排放，达到经济社会发展与生态环境保护双赢的一种经济发展形态。', 'http://www.6788.cn/bscyw/upfiles/0125/1f0aaff5/fery/w1tg.jpg', null, '', '10', '0');
INSERT INTO `cms_topic` VALUES ('4', null, '专题测试', null, '专题测试', '专题测试专题测试专题测试专题测试', null, null, '', '10', '1');
