  `start_time` date DEFAULT NULL COMMENT '开始时间',
  `end_time` date DEFAULT NULL COMMENT '结束时间',
  `is_enabled` char(1) NOT NULL DEFAULT '1' COMMENT '是否启用',
  PRIMARY KEY (`advertising_id`),
  KEY `fk_cms_advertising_site` (`site_id`),
  KEY `fk_cms_space_advertising` (`adspace_id`),
  CONSTRAINT `fk_cms_advertising_site` FOREIGN KEY (`site_id`) REFERENCES `cms_site` (`site_id`),
  CONSTRAINT `fk_cms_space_advertising` FOREIGN KEY (`adspace_id`) REFERENCES `cms_advertising_space` (`adspace_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='CMS广告表';

