  KEY `fk_cms_params_advertising` (`advertising_id`),
  CONSTRAINT `fk_cms_params_advertising` FOREIGN KEY (`advertising_id`) REFERENCES `cms_advertising` (`advertising_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='CMS广告属性表';

