-- ----------------------------
-- Table structure for `cms_channel`
-- ----------------------------
DROP TABLE IF EXISTS `cms_channel`;
CREATE TABLE `cms_channel` (
  `channel_id` int(11) NOT NULL AUTO_INCREMENT,
  `model_id` int(11) NOT NULL COMMENT '模型ID',
  `site_id` int(11) NOT NULL COMMENT '站点ID',
  `parent_id` int(11) DEFAULT NULL COMMENT '父栏目ID',
  `channel_path` varchar(30) DEFAULT NULL COMMENT '访问路径',
  `lft` int(11) NOT NULL DEFAULT '1' COMMENT '树左边',
  `rgt` int(11) NOT NULL DEFAULT '2' COMMENT '树右边',
  `priority` int(11) NOT NULL DEFAULT '10' COMMENT '排列顺序',
  `has_content` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否有内容',
  `is_display` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否显示',
  PRIMARY KEY (`channel_id`),
  KEY `fk_cms_channel_model` (`model_id`),
  KEY `fk_cms_channel_parent` (`parent_id`),
  KEY `fk_cms_channel_site` (`site_id`),
  CONSTRAINT `fk_cms_channel_model` FOREIGN KEY (`model_id`) REFERENCES `cms_model` (`model_id`),
  CONSTRAINT `fk_cms_channel_parent` FOREIGN KEY (`parent_id`) REFERENCES `cms_channel` (`channel_id`),
  CONSTRAINT `fk_cms_channel_site` FOREIGN KEY (`site_id`) REFERENCES `cms_site` (`site_id`)
) ENGINE=InnoDB AUTO_INCREMENT=58 DEFAULT CHARSET=utf8 COMMENT='CMS栏目表';

