  `channel_id` int(11) NOT NULL,
  `department_id` int(11) NOT NULL,
  PRIMARY KEY (`channel_id`,`department_id`),
  KEY `fk_cms_channel_department` (`department_id`),
  CONSTRAINT `fk_cms_channel_department` FOREIGN KEY (`department_id`) REFERENCES `cms_department` (`depart_id`),
  CONSTRAINT `fk_cms_department_channel` FOREIGN KEY (`channel_id`) REFERENCES `cms_channel` (`channel_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='CMS栏目部门关联表';

