-- Table structure for `cms_comment`
-- ----------------------------
DROP TABLE IF EXISTS `cms_comment`;
CREATE TABLE `cms_comment` (
  `comment_id` int(11) NOT NULL AUTO_INCREMENT,
  `comment_user_id` int(11) DEFAULT NULL COMMENT '评论用户ID',
  `reply_user_id` int(11) DEFAULT NULL COMMENT '回复用户ID',
  `content_id` int(11) NOT NULL COMMENT '内容ID',
  `site_id` int(11) NOT NULL COMMENT '站点ID',
  `create_time` datetime NOT NULL COMMENT '评论时间',
  `reply_time` datetime DEFAULT NULL COMMENT '回复时间',
  `ups` smallint(6) NOT NULL DEFAULT '0' COMMENT '支持数',
  `downs` smallint(6) NOT NULL DEFAULT '0' COMMENT '反对数',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `is_checked` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否审核',
  PRIMARY KEY (`comment_id`),
  KEY `fk_cms_comment_content` (`content_id`),
  KEY `fk_cms_comment_reply` (`reply_user_id`),
  KEY `fk_cms_comment_site` (`site_id`),
  KEY `fk_cms_comment_user` (`comment_user_id`),
  CONSTRAINT `fk_cms_comment_content` FOREIGN KEY (`content_id`) REFERENCES `cms_content` (`content_id`),
  CONSTRAINT `fk_cms_comment_reply` FOREIGN KEY (`reply_user_id`) REFERENCES `cms_user` (`user_id`),
  CONSTRAINT `fk_cms_comment_site` FOREIGN KEY (`site_id`) REFERENCES `cms_site` (`site_id`),
  CONSTRAINT `fk_cms_comment_user` FOREIGN KEY (`comment_user_id`) REFERENCES `cms_user` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COMMENT='CMS评论表';

