  KEY `fk_cms_attr_config` (`config_id`),
  CONSTRAINT `fk_cms_attr_config` FOREIGN KEY (`config_id`) REFERENCES `cms_config` (`config_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='CMS配置属性表';

