  PRIMARY KEY (`id`),
  KEY `fk_cms_permission_role` (`role_id`),
  CONSTRAINT `fk_cms_permission_role` FOREIGN KEY (`role_id`) REFERENCES `cms_role` (`role_id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COMMENT='CMS角色授权表';

