  `content_id` int(11) NOT NULL DEFAULT '0' COMMENT '内容id',
  PRIMARY KEY (`user_id`,`content_id`),
  KEY `fk_cms_user_collection_con` (`content_id`),
  CONSTRAINT `fk_cms_user_collection_con` FOREIGN KEY (`content_id`) REFERENCES `cms_content` (`content_id`),
  CONSTRAINT `fk_cms_user_collection_user` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户收藏关联表';

