  `user_id` int(11) NOT NULL,
  PRIMARY KEY (`department_id`,`user_id`),
  KEY `fk_cms_department_user` (`user_id`),
  CONSTRAINT `fk_cms_department_user` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`user_id`),
  CONSTRAINT `fk_cms_user_department` FOREIGN KEY (`department_id`) REFERENCES `cms_department` (`depart_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='CMS用户部门关联表';

