-- Records of cms_user_department
-- ----------------------------

-- ----------------------------
-- Table structure for `cms_user_ext`
-- ----------------------------
DROP TABLE IF EXISTS `cms_user_ext`;
CREATE TABLE `cms_user_ext` (
  `user_id` int(11) NOT NULL,
  `realname` varchar(100) DEFAULT NULL COMMENT '真实姓名',
  `gender` tinyint(1) DEFAULT NULL COMMENT '性别',
  `birthday` datetime DEFAULT NULL COMMENT '出生日期',
  `intro` varchar(255) DEFAULT NULL COMMENT '个人介绍',
  `comefrom` varchar(150) DEFAULT NULL COMMENT '来自',
  `qq` varchar(100) DEFAULT NULL COMMENT 'QQ',
  `msn` varchar(100) DEFAULT NULL COMMENT 'MSN',
  `phone` varchar(50) DEFAULT NULL COMMENT '电话',
  `mobile` varchar(50) DEFAULT NULL COMMENT '手机',
  `user_img` varchar(255) DEFAULT NULL COMMENT '用户头像',
  `user_signature` varchar(255) DEFAULT NULL COMMENT '用户个性签名',
  PRIMARY KEY (`user_id`),
  CONSTRAINT `fk_cms_ext_user` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='CMS用户扩展信息表';

-- ----------------------------
-- Records of cms_user_ext
-- ----------------------------

-- ----------------------------
-- Table structure for `cms_user_role`
-- ----------------------------
DROP TABLE IF EXISTS `cms_user_role`;
CREATE TABLE `cms_user_role` (
  `role_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  PRIMARY KEY (`role_id`,`user_id`),
  KEY `fk_cms_role_user` (`user_id`),
  CONSTRAINT `fk_cms_role_user` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`user_id`),
  CONSTRAINT `fk_cms_user_role` FOREIGN KEY (`role_id`) REFERENCES `cms_role` (`role_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='CMS用户角色关联表';

-- ----------------------------
-- Records of cms_user_role
-- ----------------------------

-- ----------------------------
-- Table structure for `cms_user_site`
-- ----------------------------
DROP TABLE IF EXISTS `cms_user_site`;
CREATE TABLE `cms_user_site` (
  `usersite_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `site_id` int(11) NOT NULL,
  `check_step` tinyint(4) NOT NULL DEFAULT '1' COMMENT '审核级别',
  `is_all_channel` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否拥有所有栏目的权限',
  PRIMARY KEY (`usersite_id`),
  KEY `fk_cms_site_user` (`user_id`),
  KEY `fk_cms_user_site` (`site_id`),
  CONSTRAINT `fk_cms_site_user` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`user_id`),
  CONSTRAINT `fk_cms_user_site` FOREIGN KEY (`site_id`) REFERENCES `cms_site` (`site_id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='CMS管理员站点表';

-- ----------------------------
-- Records of cms_user_site
-- ----------------------------

-- ----------------------------
-- Table structure for `cms_vote_item`
-- ----------------------------
DROP TABLE IF EXISTS `cms_vote_item`;
CREATE TABLE `cms_vote_item` (
  `voteitem_id` int(11) NOT NULL AUTO_INCREMENT,
  `votetopic_id` int(11) NOT NULL,
  `title` varchar(255) NOT NULL COMMENT '标题',
  `vote_count` int(11) NOT NULL DEFAULT '0' COMMENT '投票数量',
  `priority` int(11) NOT NULL DEFAULT '10' COMMENT '排列顺序',
  PRIMARY KEY (`voteitem_id`),
  KEY `fk_cms_vote_item_topic` (`votetopic_id`),
  CONSTRAINT `fk_cms_vote_item_topic` FOREIGN KEY (`votetopic_id`) REFERENCES `cms_vote_topic` (`votetopic_id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COMMENT='CMS投票项';

-- ----------------------------
-- Records of cms_vote_item
-- ----------------------------

-- ----------------------------
-- Table structure for `cms_vote_record`
-- ----------------------------
DROP TABLE IF EXISTS `cms_vote_record`;
CREATE TABLE `cms_vote_record` (
  `voterecored_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL,
  `votetopic_id` int(11) NOT NULL,
  `vote_time` datetime NOT NULL COMMENT '投票时间',
  `vote_ip` varchar(50) NOT NULL COMMENT '投票IP',
  `vote_cookie` varchar(32) NOT NULL COMMENT '投票COOKIE',
  PRIMARY KEY (`voterecored_id`),
  KEY `fk_cms_vote_record_topic` (`votetopic_id`),
  KEY `fk_cms_voterecord_user` (`user_id`),
  CONSTRAINT `fk_cms_voterecord_user` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`user_id`),
  CONSTRAINT `fk_cms_vote_record_topic` FOREIGN KEY (`votetopic_id`) REFERENCES `cms_vote_topic` (`votetopic_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='CMS投票记录';

-- ----------------------------
-- Records of cms_vote_record
-- ----------------------------

-- ----------------------------
-- Table structure for `cms_vote_topic`
-- ----------------------------
DROP TABLE IF EXISTS `cms_vote_topic`;
CREATE TABLE `cms_vote_topic` (
  `votetopic_id` int(11) NOT NULL AUTO_INCREMENT,
  `site_id` int(11) NOT NULL,
  `title` varchar(255) NOT NULL COMMENT '标题',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '结束时间',
  `repeate_hour` int(11) DEFAULT NULL COMMENT '重复投票限制时间，单位小时，为空不允许重复投票',
  `total_count` int(11) NOT NULL DEFAULT '0' COMMENT '总投票数',
  `multi_select` int(11) NOT NULL DEFAULT '1' COMMENT '最多可以选择几项',
  `is_restrict_member` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否限制会员',
  `is_restrict_ip` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否限制IP',
  `is_restrict_cookie` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否限制COOKIE',
  `is_disabled` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否禁用',
  `is_def` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否默认主题',
  PRIMARY KEY (`votetopic_id`),
  KEY `fk_cms_votetopic_site` (`site_id`),
  CONSTRAINT `fk_cms_votetopic_site` FOREIGN KEY (`site_id`) REFERENCES `cms_site` (`site_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='CMS投票主题';

-- ----------------------------
-- Records of cms_vote_topic
-- ----------------------------

-- ----------------------------
-- Table structure for `yk_authentication`
-- ----------------------------
DROP TABLE IF EXISTS `yk_authentication`;
CREATE TABLE `yk_authentication` (
  `authentication_id` char(32) NOT NULL COMMENT '认证ID',
  `userid` int(11) NOT NULL COMMENT '用户ID',
  `username` varchar(100) NOT NULL COMMENT '用户名',
  `email` varchar(100) DEFAULT NULL COMMENT '邮箱',
  `login_time` datetime NOT NULL COMMENT '登录时间',
  `login_ip` varchar(50) NOT NULL COMMENT '登录ip',
  `update_time` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`authentication_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='认证信息表';

